import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import "./Expenses.css";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState(2021);

  const fiteredExpenses = props.items.filter(expense =>{
    return expense.date.getFullYear().toString() === filteredYear;
  })

  const filterHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter selected={filteredYear} onFilter={filterHandler} />
        {fiteredExpenses.items.map((pushedExpense) => (
          <ExpenseItem
            key={pushedExpense.id}
            title={pushedExpense.title}
            amount={pushedExpense.amount}
            date={pushedExpense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
